// Reports page with PDF and Excel export
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { FileDown, FileSpreadsheet, Filter } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import type { Sale } from "@shared/schema";
import { Skeleton } from "@/components/ui/skeleton";

export default function Reports() {
  const [category, setCategory] = useState<string>("all");
  const [startDate, setStartDate] = useState<string>("");
  const [endDate, setEndDate] = useState<string>("");

  const { data: sales, isLoading } = useQuery<Sale[]>({
    queryKey: ["/api/sales", { category, startDate, endDate }],
  });

  const formatCurrency = (value: string | number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(Number(value));
  };

  const formatDate = (date: string | Date) => {
    return new Date(date).toLocaleDateString('pt-BR');
  };

  const getCategoryLabel = (cat: string) => {
    const labels: Record<string, string> = {
      plr_nacional: "PLR Nacional",
      plr_internacional: "PLR Internacional",
      clothing: "Marca de Roupas",
    };
    return labels[cat] || cat;
  };

  const totalRevenue = sales?.reduce((sum, sale) => sum + Number(sale.totalRevenue), 0) || 0;
  const totalCost = sales?.reduce((sum, sale) => sum + Number(sale.totalCost), 0) || 0;
  const totalProfit = sales?.reduce((sum, sale) => sum + Number(sale.profit), 0) || 0;

  const handleExportPDF = async () => {
    // Dynamic import to avoid loading jsPDF on initial page load
    const jsPDF = (await import('jspdf')).default;
    const doc = new jsPDF();

    doc.setFontSize(18);
    doc.text('Relatório de Vendas', 14, 20);
    doc.setFontSize(11);
    doc.text(`Gerado em: ${new Date().toLocaleDateString('pt-BR')}`, 14, 30);

    if (category !== 'all') {
      doc.text(`Categoria: ${getCategoryLabel(category)}`, 14, 36);
    }
    if (startDate || endDate) {
      doc.text(`Período: ${startDate || 'início'} até ${endDate || 'hoje'}`, 14, 42);
    }

    doc.text(`Total de Receita: ${formatCurrency(totalRevenue)}`, 14, 52);
    doc.text(`Total de Custos: ${formatCurrency(totalCost)}`, 14, 58);
    doc.text(`Lucro Total: ${formatCurrency(totalProfit)}`, 14, 64);

    let yPos = 74;
    doc.setFontSize(9);
    doc.text('Produto', 14, yPos);
    doc.text('Categoria', 70, yPos);
    doc.text('Qtd', 120, yPos);
    doc.text('Lucro', 140, yPos);
    doc.text('Data', 170, yPos);
    yPos += 6;

    sales?.forEach((sale, index) => {
      if (yPos > 280) {
        doc.addPage();
        yPos = 20;
      }
      doc.text(sale.productName.substring(0, 30), 14, yPos);
      doc.text(getCategoryLabel(sale.category), 70, yPos);
      doc.text(String(sale.quantity), 120, yPos);
      doc.text(formatCurrency(sale.profit), 140, yPos);
      doc.text(formatDate(sale.saleDate), 170, yPos);
      yPos += 6;
    });

    doc.save(`relatorio-${Date.now()}.pdf`);
  };

  const handleExportExcel = async () => {
    const XLSX = await import('xlsx');

    const data = sales?.map(sale => ({
      'Produto': sale.productName,
      'Categoria': getCategoryLabel(sale.category),
      'Quantidade': sale.quantity,
      'Custo Unitário': Number(sale.unitCost),
      'Preço Unitário': Number(sale.unitPrice),
      'Custo Total': Number(sale.totalCost),
      'Receita Total': Number(sale.totalRevenue),
      'Lucro': Number(sale.profit),
      'Data': formatDate(sale.saleDate),
    })) || [];

    data.push({
      'Produto': '',
      'Categoria': 'TOTAIS',
      'Quantidade': '',
      'Custo Unitário': '',
      'Preço Unitário': '',
      'Custo Total': totalCost,
      'Receita Total': totalRevenue,
      'Lucro': totalProfit,
      'Data': '',
    } as any);

    const ws = XLSX.utils.json_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Relatório');
    XLSX.writeFile(wb, `relatorio-${Date.now()}.xlsx`);
  };

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-4xl font-bold mb-2">Relatórios</h1>
        <p className="text-muted-foreground text-lg">
          Exporte relatórios detalhados em PDF ou Excel
        </p>
      </div>

      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Filter className="h-5 w-5" />
            Filtros
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="space-y-2">
              <Label>Categoria</Label>
              <Select value={category} onValueChange={setCategory}>
                <SelectTrigger data-testid="select-category">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todas</SelectItem>
                  <SelectItem value="plr_nacional">PLR Nacional</SelectItem>
                  <SelectItem value="plr_internacional">PLR Internacional</SelectItem>
                  <SelectItem value="clothing">Marca de Roupas</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label>Data Inicial</Label>
              <Input
                type="date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                data-testid="input-start-date"
              />
            </div>
            <div className="space-y-2">
              <Label>Data Final</Label>
              <Input
                type="date"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
                data-testid="input-end-date"
              />
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card>
          <CardHeader>
            <CardTitle className="text-sm font-medium text-muted-foreground">
              Receita Total
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="total-revenue">
              {formatCurrency(totalRevenue)}
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader>
            <CardTitle className="text-sm font-medium text-muted-foreground">
              Custos Totais
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="total-costs">
              {formatCurrency(totalCost)}
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader>
            <CardTitle className="text-sm font-medium text-muted-foreground">
              Lucro Total
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-primary" data-testid="total-profit">
              {formatCurrency(totalProfit)}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Export Buttons */}
      <div className="flex gap-3">
        <Button onClick={handleExportPDF} data-testid="button-export-pdf">
          <FileDown className="mr-2 h-4 w-4" />
          Exportar PDF
        </Button>
        <Button variant="outline" onClick={handleExportExcel} data-testid="button-export-excel">
          <FileSpreadsheet className="mr-2 h-4 w-4" />
          Exportar Excel
        </Button>
      </div>

      {/* Sales Table */}
      <Card>
        <CardHeader>
          <CardTitle>Vendas Registradas</CardTitle>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="space-y-3">
              {[1, 2, 3, 4, 5].map((i) => <Skeleton key={i} className="h-12 w-full" />)}
            </div>
          ) : sales && sales.length > 0 ? (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Produto</TableHead>
                  <TableHead>Categoria</TableHead>
                  <TableHead>Quantidade</TableHead>
                  <TableHead>Custo Total</TableHead>
                  <TableHead>Receita Total</TableHead>
                  <TableHead>Lucro</TableHead>
                  <TableHead>Data</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {sales.map((sale) => (
                  <TableRow key={sale.id} data-testid={`row-sale-${sale.id}`}>
                    <TableCell className="font-medium">{sale.productName}</TableCell>
                    <TableCell>{getCategoryLabel(sale.category)}</TableCell>
                    <TableCell>{sale.quantity}</TableCell>
                    <TableCell>{formatCurrency(sale.totalCost)}</TableCell>
                    <TableCell>{formatCurrency(sale.totalRevenue)}</TableCell>
                    <TableCell className="font-semibold text-primary">
                      {formatCurrency(sale.profit)}
                    </TableCell>
                    <TableCell>{formatDate(sale.saleDate)}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          ) : (
            <div className="text-center py-12">
              <p className="text-muted-foreground">
                Nenhuma venda registrada no período selecionado.
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

