// Clothing/Marca de Roupas products management page
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Plus, Pencil, Trash2, AlertCircle } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { ProductFormDialog, type ProductFormField } from "@/components/product-form-dialog";
import { insertClothingProductSchema, type ClothingProduct } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Skeleton } from "@/components/ui/skeleton";

const formFields: ProductFormField[] = [
  { name: "name", label: "Nome da Peça", type: "text", placeholder: "Ex: Camiseta Premium" },
  { name: "productionCost", label: "Custo de Produção (R$)", type: "number", placeholder: "0.00", step: "0.01" },
  { name: "salePrice", label: "Preço de Revenda (R$)", type: "number", placeholder: "0.00", step: "0.01" },
  { name: "stockQuantity", label: "Quantidade em Estoque", type: "number", placeholder: "0" },
  { name: "quantitySold", label: "Quantidade Vendida", type: "number", placeholder: "0" },
];

export default function Clothing() {
  const [dialogOpen, setDialogOpen] = useState(false);
  const [editingProduct, setEditingProduct] = useState<ClothingProduct | null>(null);
  const { toast } = useToast();

  const { data: products, isLoading } = useQuery<ClothingProduct[]>({
    queryKey: ["/api/clothing/products"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      if (editingProduct) {
        await apiRequest("PUT", `/api/clothing/products/${editingProduct.id}`, data);
      } else {
        await apiRequest("POST", "/api/clothing/products", data);
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clothing/products"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/daily-profit"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/category-comparison"] });
      setDialogOpen(false);
      setEditingProduct(null);
      toast({
        title: "Sucesso",
        description: editingProduct ? "Peça atualizada com sucesso!" : "Peça adicionada com sucesso!",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Não autorizado",
          description: "Você foi desconectado. Fazendo login novamente...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Erro",
        description: editingProduct ? "Falha ao atualizar peça." : "Falha ao adicionar peça.",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/clothing/products/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clothing/products"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/daily-profit"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/category-comparison"] });
      toast({
        title: "Sucesso",
        description: "Peça excluída com sucesso!",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Não autorizado",
          description: "Você foi desconectado. Fazendo login novamente...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Erro",
        description: "Falha ao excluir peça.",
        variant: "destructive",
      });
    },
  });

  const formatCurrency = (value: string | number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(Number(value));
  };

  const calculateProfit = (product: ClothingProduct) => {
    const revenue = Number(product.salePrice) * Number(product.quantitySold);
    const cost = Number(product.productionCost) * Number(product.quantitySold);
    return revenue - cost;
  };

  const getStockStatus = (quantity: number) => {
    if (quantity === 0) return { label: "Sem Estoque", variant: "destructive" as const };
    if (quantity < 10) return { label: "Estoque Baixo", variant: "secondary" as const };
    return { label: "Em Estoque", variant: "default" as const };
  };

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-4xl font-bold mb-2">Marca de Roupas</h1>
          <p className="text-muted-foreground text-lg">
            Gerencie seu estoque e vendas de roupas
          </p>
        </div>
        <Button onClick={() => {
          setEditingProduct(null);
          setDialogOpen(true);
        }} data-testid="button-add-product">
          <Plus className="mr-2 h-4 w-4" />
          Adicionar Peça
        </Button>
      </div>

      {/* Products Table */}
      <Card>
        <CardHeader>
          <CardTitle>Peças Cadastradas</CardTitle>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="space-y-3">
              {[1, 2, 3].map((i) => <Skeleton key={i} className="h-12 w-full" />)}
            </div>
          ) : products && products.length > 0 ? (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Peça</TableHead>
                  <TableHead>Custo Produção</TableHead>
                  <TableHead>Preço Revenda</TableHead>
                  <TableHead>Estoque</TableHead>
                  <TableHead>Qtd. Vendida</TableHead>
                  <TableHead>Lucro Total</TableHead>
                  <TableHead className="text-right">Ações</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {products.map((product) => {
                  const stockStatus = getStockStatus(product.stockQuantity);
                  return (
                    <TableRow key={product.id} data-testid={`row-product-${product.id}`}>
                      <TableCell className="font-medium">{product.name}</TableCell>
                      <TableCell>{formatCurrency(product.productionCost)}</TableCell>
                      <TableCell>{formatCurrency(product.salePrice)}</TableCell>
                      <TableCell>
                        <div className="flex items-center gap-2">
                          <span>{product.stockQuantity}</span>
                          <Badge variant={stockStatus.variant} className="text-xs">
                            {product.stockQuantity === 0 && <AlertCircle className="h-3 w-3 mr-1" />}
                            {stockStatus.label}
                          </Badge>
                        </div>
                      </TableCell>
                      <TableCell>{product.quantitySold}</TableCell>
                      <TableCell className="font-semibold text-primary">
                        {formatCurrency(calculateProfit(product))}
                      </TableCell>
                      <TableCell className="text-right">
                        <div className="flex gap-2 justify-end">
                          <Button
                            variant="ghost"
                            size="icon"
                            onClick={() => {
                              setEditingProduct(product);
                              setDialogOpen(true);
                            }}
                            data-testid={`button-edit-${product.id}`}
                          >
                            <Pencil className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="icon"
                            onClick={() => deleteMutation.mutate(product.id)}
                            data-testid={`button-delete-${product.id}`}
                          >
                            <Trash2 className="h-4 w-4 text-destructive" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          ) : (
            <div className="text-center py-12">
              <p className="text-muted-foreground">
                Nenhuma peça cadastrada ainda. Clique em "Adicionar Peça" para começar.
              </p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Add/Edit Product Dialog */}
      <ProductFormDialog
        open={dialogOpen}
        onOpenChange={(open) => {
          setDialogOpen(open);
          if (!open) setEditingProduct(null);
        }}
        title={editingProduct ? "Editar Peça de Roupa" : "Adicionar Peça de Roupa"}
        fields={formFields}
        schema={insertClothingProductSchema}
        onSubmit={(data) => createMutation.mutate(data)}
        isLoading={createMutation.isPending}
        defaultValues={editingProduct ? {
          name: editingProduct.name,
          productionCost: editingProduct.productionCost,
          salePrice: editingProduct.salePrice,
          stockQuantity: editingProduct.stockQuantity.toString(),
          quantitySold: editingProduct.quantitySold.toString(),
        } : undefined}
      />
    </div>
  );
}

